
addEventListener('load', ()=>{

  let btn = document.getElementById("sidebarToggle")
  let sidebar = document.getElementById("accordionSidebar")
  btn?.addEventListener('click', ()=>{
    sidebar.classList.toggle("toggled")
  })

  let body = document.body
  let btnToggleSidebar = document.getElementById("sidebarToggleTop")
  let ulSideBar = document.getElementById("accordionSidebar")
  let Contenedor = document.getElementById("Contenedor");

  body.classList.add('sidebar-toggled')
  ulSideBar.classList.add('toggled')
  btnToggleSidebar.addEventListener("click", ()=>{
    body.classList.toggle('sidebar-toggled')
    ulSideBar.classList.toggle('toggled')
  })
  Contenedor.addEventListener("click", ()=>{
    let Div=document.getElementsByClassName('collapse show')
    if(Div[0]){
      //alert(Div[0].id)
      if(Div[0].id!='collapseCard'){
        //alert("Es Distinto")
        Div[0].classList.toggle('show')
      }else{
        Div[1].classList.toggle('show')
      }

      // if (document.getElementById('collapseCard')!=Div[0]){
      //   //alert('caso 1')
      //   //Div[0].classList.toggle('show')
      // }else{
      //   //alert('caso 2')
      //   //Div[1].classList.toggle('show')
      // }
    }
  })
});






